create function fn_transaction_id()
  returns varchar(50) NOT DETERMINISTIC READS SQL DATA
  begin
     declare comm_name varchar(50);
     declare comm_value varchar(50);
     declare comm_cur cursor for show status like 'Com_commit';
     if @@autocommit = 0 then
          open comm_cur;
          fetch comm_cur into comm_name, comm_value;
          close comm_cur;
          return concat(concat(connection_id(), '.'), comm_value);
     else
          return null;
     end if;
  end


create trigger $(triggerName) after insert on $(schemaName)$(tableName)
for each row begin
  if $(syncOnInsertCondition) and $(syncOnIncomingBatchCondition) then
    insert into $(defaultCatalog)$(prefixName)_data (table_name, event_type, trigger_hist_id, row_data, create_time)
    values(
      '$(targetTableName)',
      'I',
      $(triggerHistoryId),                                      
      concat($(columns)
       ),
      CURRENT_TIMESTAMP
    );
    insert into $(defaultCatalog)$(prefixName)_data_event (node_id, data_id, channel_id, transaction_id) (select node_id, last_insert_id(), '$(channelName)', $(txIdExpression)  from $(defaultCatalog)$(prefixName)_node c where 
    c.node_group_id='$(targetGroupId)' and c.sync_enabled=1 $(nodeSelectWhere));
  end if;
end


create trigger $(triggerName) after update on $(schemaName)$(tableName)
for each row begin
  if $(syncOnUpdateCondition) and $(syncOnIncomingBatchCondition) then
    insert into $(defaultCatalog)$(prefixName)_data (table_name, event_type, trigger_hist_id, pk_data, row_data, create_time)
    values(
      '$(targetTableName)',
      'U',
      $(triggerHistoryId),
      concat($(oldKeys)
       ),
      concat($(columns)
       ),
      CURRENT_TIMESTAMP
    );
    insert into $(defaultCatalog)$(prefixName)_data_event (node_id, data_id, channel_id, transaction_id) (select node_id, last_insert_id(), '$(channelName)', $(txIdExpression) from $(defaultCatalog)$(prefixName)_node c where 
    c.node_group_id='$(targetGroupId)' and c.sync_enabled=1 $(nodeSelectWhere));
  end if;
end


create trigger $(triggerName) after delete on $(schemaName)$(tableName)
for each row begin
  if $(syncOnDeleteCondition) and $(syncOnIncomingBatchCondition) then
    insert into $(defaultCatalog)$(prefixName)_data (table_name, event_type, trigger_hist_id, pk_data, create_time)
    values(
      '$(targetTableName)',
      'D',
      $(triggerHistoryId),
      concat($(oldKeys)
       ),
      CURRENT_TIMESTAMP
    );
    insert into $(defaultCatalog)$(prefixName)_data_event (node_id, data_id, channel_id, transaction_id) (select node_id, last_insert_id(), '$(channelName)', $(txIdExpression) from $(defaultCatalog)$(prefixName)_node c where 
    c.node_group_id='$(targetGroupId)' and c.sync_enabled=1 $(nodeSelectWhere));
  end if;
end
